import * as auth from './reducers/auth.reducers';
import * as quote from '../../quote/quote-store/reducers/quote.reducer';
import { createFeatureSelector } from '@ngrx/store';
export interface AppState {
  authState: auth.State;
  quote: quote.State;
}

export const reducers = {
  auth: auth.reducer,
  quote: quote.reducer,
};

export const selectAuthState = createFeatureSelector<AppState>('auth');
export const quoteState = createFeatureSelector<AppState>('quote');

export interface Quote {
     quote: string;
     description: string;
     author: string;
     category: string;
     id?: number;
}

import { Quote } from '../../core/models/quote';
export interface QuoteState {
  readonly quotes: Quote[];
}

import { Quote } from '../../../core/models/quote';
import {
  QuoteActions,
  ADD_QUOTE,
  GET_QUOTES,
  DELETE_QUOTES,
  UPDATE_QUOTES,
  DATA_LOAD,
  ADD_QUOTE_SUCCESS,
  UPDATE_QUOTE_SUCCESS,
  DELETE_QUOTE_SUCCESS,
} from '../actions/quote.actions';

export interface State {
  quote: Quote[];
  success: boolean | null;
  message: string | null;
}

export const initialState: State = {
  quote: [],
  message: null,
  success: null,
};
export function reducer(
  state: State = initialState,
  action: QuoteActions
): State {
  switch (action.type) {
    case ADD_QUOTE: {
      return {
        ...state,
      };
    }
    case GET_QUOTES: {
      return {
        ...state,
      };
    }
    case DELETE_QUOTES: {
      return {
        ...state,
      };
    }
    case UPDATE_QUOTES: {
      return {
        ...state,
      };
    }
    case DATA_LOAD: {
      return {
        quote: action.payload,
        message: null,
        success: null,
      };
    }
    case ADD_QUOTE_SUCCESS: {
      return {
        quote: [...state.quote, action.payload],
        message: 'The quote is added successfully!',
        success: true,
      };
    }
    case UPDATE_QUOTE_SUCCESS: {
      return {
        ...state,
        message: 'The quote is updated successfully!',
        success: true,
      };
    }
    case DELETE_QUOTE_SUCCESS: {
      return {
        ...state,
      };
    }
    default: {
      return state;
    }
  }
}

import { User } from '../../../core/models/User';
import { AuthActionTypes, AuthAction } from '../actions/user.actions';

export interface State {
  isAuthenticated: boolean;
  user: User | null;
  errorMessage: string | null;
}

export const initialState: State = {
  isAuthenticated: false,
  user: null,
  errorMessage: null,
};
export function reducer(state = initialState, action: AuthAction): State {
  switch (action.type) {
    case AuthActionTypes.LOGIN_SUCCESS: {
      return {
        ...state,
        isAuthenticated: true,
        user: {
          email: action.payload.email,
          password: action.payload.password,
        },
        errorMessage: null,
      };
    }
    case AuthActionTypes.LOGIN_FAILURE: {
      return {
        ...state,
        errorMessage: 'Incorrect email and/or password.',
      };
    }
    case AuthActionTypes.SIGNUP_SUCCESS: {
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload,
        errorMessage: null,
      };
    }
    case AuthActionTypes.SIGNUP_FAILURE: {
      return {
        ...state,
        errorMessage: 'That email is already in use.',
      };
    }
    case AuthActionTypes.LOGOUT: {
      return initialState;
    }
    default: {
      return state;
    }
  }
}

export interface User {
    id?: number;
    name?: string;
    email: string;
    password: string;
}

